mapper.select:

select   hcod , hnam ,pgun
from TB_CA503 WITH (NOLOCK)
WHERE isnull(hcod,'')   = #{hcod}
and isnull(hnam,'')   = #{hnam}
ORDER BY hcod


select   hcod ,bgroup, bgrpnm
from TB_CA503_01 WITH (NOLOCK)
WHERE isnull(bgroup,'')   = #{bgroup}
and isnull(bgrpnm,'')   = #{bgrpnm}
ORDER BY bgroup
