mapper.select:

select   plan_no
from TB_FPLAN WITH (NOLOCK)
WHERE isnull(plan_no,'')   = #{plan_no}
ORDER BY plan_no

mapper.update:
update TB_FPLAN
set pcode = #{pcode}
, indate=#{indate}
, prod_sdate=#{prod_sdate}
, prod_edate=#{prod_edate}
, cltcd=#{cltcd}
, qcdate=#{qcdate}
, perid=#{perid}
, ecltnm=#{ecltnm}
, workdv=#{workdv}
, dem_flag=#{dem_flag}
, prod_qty=#{prod_qty}
, cls_flag=#{cls_flag}
, istore=#{istore}
, ostore=#{ostore}
, rwflag=#{rwflag}
, remark=#{remark}






























where plan_no   = #{plan_no}
