mapper.select:

select   jkey
from JCODE WITH (NOLOCK)
WHERE isnull(jpumcode,'')   = #{jpumcode}
ORDER BY jpum


select  acorp1 + CAST(acorp2 as varchar) as acode
from cif WITH (NOLOCK)
WHERE isnull(acorp,'')   = #{acorp}
ORDER BY acorp

